import React , {Component} from 'react';
import Navi from './component/Navi';
import Content from './component/Content';
import Subject from './component/Subject';
import './App.css';


class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      mode:'welcome',
      subject:{title:'WEB', sub:'World Wide Web'},
      welcome:{title:"welcome", desc:'React is.......'}, 
      contents:[
        {id:1, title:'HTML', desc:'HTML is HyperText MakeUp language!'},
        {id:2, title:'CSS', desc:'CSS is .....'},
        {id:3, title:'JavaScript', desc:'JavaScript is ....'}
      ]
    }
  }
  render(){
    var _title, _desc = null;
    if(this.state.mode === 'welcome'){
      _title = this.state.welcome.title;
      _desc = this.state.welcome.desc;
    }else if(this.state.mode === 'read'){
      _title = this.state.contents[0].title;
      _desc = this.state.contents[0].desc;
    }
    return (
      <div className="App">
        {/* <Subject 
        title={this.state.subject.title} 
        sub={this.state.subject.title}></Subject> */}
        <header>
              <h1><a href="/" onClick>{this.state.subject.title}</a></h1>
              {this.state.subject.title}
        </header>
        <Navi data={this.state.contents}></Navi>
        <Content title={_title} desc={_desc}></Content>
      </div>
    );
  }

}

export default App;
