import React , {Component} from 'react';
import Navi from './component/Navi';
import ReadContent from './component/ReadContent';
import CreateContent from './component/CreateContent';
import UpdateContent from './component/UpdateContent';
import Subject from './component/Subject';
import Control from './component/Control';
import './App.css';


class App extends Component {
  constructor(props){
    super(props);
    this.max_content_id = 3;
    this.state = {
      mode:'welcome',
      selected_content_id:2,
      subject:{title:'WEB', sub:'World Wide Web'},
      welcome:{title:"welcome", desc:'React is.......'}, 
      contents:[
        {id:1, title:'HTML', desc:'HTML is HyperText MakeUp language!'},
        {id:2, title:'CSS', desc:'CSS is .....'},
        {id:3, title:'JavaScript', desc:'JavaScript is ....'}
      ]
    }
  }
  render(){
    var _title, _desc, _article = null;
    if(this.state.mode === 'welcome'){
      _title = this.state.welcome.title;
      _desc = this.state.welcome.desc;
      _article = <ReadContent title={_title} desc={_desc}></ReadContent>
    }else if(this.state.mode === 'read'){
      var i = 0;
      while(i < this.state.contents.length){
        var data = this.state.contents[i];
        if(data.id === this.state.selected_content_id){
          _title = data.title;
          _desc = data.desc;      
          break;
        }
        i = i + 1;
      }
      _article = <ReadContent title={_title} desc={_desc}></ReadContent>
    }else if(this.state.mode === 'create'){
      _article = <CreateContent onSubmit={
        (_title, _desc)=>{
          this.max_content_id = this.max_content_id + 1; 
          const con = this.state.contents.concat(
            {id:this.max_content_id, title:_title, desc:_desc},
          );
          this.setState({
            contents:con
          });
        } 
      }></CreateContent>
    }else if(this.state.mode === 'update'){
      _article = <CreateContent onSubmit={
        (_title, _desc)=>{
          this.max_content_id = this.max_content_id + 1; 
          const con = this.state.contents.concat(
            {id:this.max_content_id, title:_title, desc:_desc},
          );
          this.setState({
            contents:con
          });
        } 
      }></CreateContent>
    }
    return (
      <div className="App">
        <Subject 
        title={this.state.subject.title} 
        sub={this.state.subject.title}
        onChangePage={
          ()=>{
            this.setState({
              mode:'welcome',
            });
          }
        }
        ></Subject>
        <Navi onChangePage={
          (id)=>{
            this.setState({
              mode:'read',
              selected_content_id:Number(id),
            })
          }
        } data={this.state.contents}></Navi>
        <Control onChangeMode={
          (mode)=>{
            this.setState({
              mode:mode,
            })
          }
        }></Control>
        {_article}
      </div>
    );
  }

}

export default App;
